import React, { Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import logoutAction from '../actions/logoutAction'
// import SweetAlert from 'sweetalert-react'
import 'sweetalert/dist/sweetalert.css'
import Swal from 'sweetalert2'

class Logout extends Component {
    constructor() {
        super()
        this.state = {
            msg: "",
            title: "",
            text: "",
            showAlert: false
        }
    }

    componentWillReceiveProps(newProps) {
        console.log(newProps.logout.msg)
        if (newProps.logout.msg === "logoutSuccess") {

            const Toast = Swal.mixin({
                toast: true,
                position: 'top-end',
                showConfirmButton: false,
                timer: 3000
            });

            Toast.fire({
                type: 'success',
                title: 'Logged out successfully'
            })
            this.props.history.push('/')
        }
    }

    logoutSubmit = (event) => {
        event.preventDefault()
        this.props.logoutAction({state})
    }

    render() {
        return (
            <form className="col s12" onSubmit={this.logoutSubmit}>
                <div>
                    <button className="btn green darken-2 waves-effect waves-light" type="submit" name="action">Logout
                        <i className="material-icons right">send</i>
                    </button>
                </div>
            </form>
        )
    }
}

function mapStatetoProps(state) {
    return {
        logout: state.logout
    }
}
function mapDispatchtoProps(dispatcher) {
    return bindActionCreators({
        logoutAction: logoutAction
    }, dispatcher)
}


export default connect(mapStatetoProps, mapDispatchtoProps)(Logout)
