import axios from 'axios'

export default (formData) => {
    const paymentPromise = axios({
        url: `${window.apiHost}/payments`,
        method: 'POST',
        data: formData
    })
    return {
        type: "REGISTER_ACTION",
        payload: paymentPromise
    }
}
